mapper.update:
update orders 
set ord_number = concat('ord-', year(#{ord_date}), '-', lpad(#{ord_count}, 4, '0')) 
where ord_count = #{ord_count}

update orders 
set ord_delete_status = '02' 
where ord_count = #{ord_count}

update orders SET ord_count = #{ord_count}, ord_number = #{ord_number}, ord_status = '01', ord_manager_id = #{ord_manager_id}, ord_supervisor_id = #{ord_supervisor_id}, prod_id = #{prod_id}, ord_price = #{ord_price}, ord_quantity = #{ord_quantity},
ord_date_change = now(),
ord_text = #{ord_text}, wh_number = #{wh_number} WHERE ord_count = #{ord_count}

