mapper.update:
UPDATE receiving_info
SET rcv_status = '03'
WHERE rcv_number = #{rcv_number};

mapper.insert:
INSERT INTO stock (prod_id, wh_number, prod_qty)
VALUES (#{prod_id}, #{wh_number}, #{rcv_quantity})
ON DUPLICATE KEY UPDATE prod_qty = prod_qty + #{rcv_quantity};

mapper.update:
UPDATE receiving_info
SET rcv_status = '04'
WHERE rcv_number = #{rcv_number};
